import React, { useState } from 'react';

const InputComponent = () => {
  const containerStyle = {
    padding: '30px',
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column', // Corrected property name
    alignItems: 'center', // Center items horizontally
  };

  const inputStyle = {
    padding: '0.5em',
    width: '80%',
    borderRadius: '15px',
    height: '2em',
    animation: 'seIn 4s ease-out',
  };

  const buttonStyle = {
    fontSize: '1em',
    padding: '10px',
    animation: 'easeIn 2s ease-out',
    backgroundColor: '#190482',
    color: '#C2D9FF',
    borderRadius: '5px',
    borderColor: '#7752FE',
    marginTop: '10px', // Adjusted margin to separate the input and button
  };

  // Define the CSS keyframes for the ease-in animation
  const styles = `
    @keyframes seIn {
      0% { opacity: 0; transform: translateY(-120px); }
      100% { opacity: 1; transform: translateY(0); }
    }
  `;

  return (
    <div style={containerStyle}>
      <style>{styles}</style>
      <input type="text" style={inputStyle} placeholder="Enter the URL" />
      <button style={buttonStyle}>Click to Shorten</button>
    </div>
  );
};

export default InputComponent;
