import React from 'react';

const Url = () => {
        const containerStyle = {
          display: 'flex',  // Make sure it's a flex container
          alignItems: 'center', 
          justifyContent: 'center', // Adjust as needed
        };
      
        const buttonStyle = {
          fontSize: '1em',
          padding: '10px',
          animation: 'easeIn 2s ease-out',
          backgroundColor:'#190482',
          color:'#C2D9FF',
          borderRadius:'5px',
          borderColor:'#7752FE'
      
        };
      
        const styls=`
      @keyframes easeIn{
          0%{opacity:0;transform:translateY(-120px);}
          100%{opacity:1;transform:translateX(-340);}
      
      }`
      ;
  const parent = {
    fontSize: '26px',
    animation: 'easeIn 2s ease-out', // Set the font size of the parent element
  };

  const head = {
    fontSize: '1em',
    textAlign: 'center',
 // ease-in is the easing function
  };

  // Define the CSS keyframes for the ease-in animation
  const containeStyle = {
    padding: '30px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  const inputStyle = {
    padding: '0.5em',
    width:'80%',
    borderRadius:'15px',
    height:'2em',
     // ease-in is the easing function
};

// Define the CSS keyframes for the ease-in animation
  return (
    <div style={parent}>
      <style>{styles}</style>
      <h1 style={head}>URL Shortener</h1>
      <div style={containeStyle}>
      <input type="text" style={inputStyle}  placeholder="Enter the URL"/>
    </div>
      <div style={containerStyle}>
        <style>{styls}</style>
      <button style={buttonStyle}>Click to Shorten</button>
    </div>
    
    </div>
  );
};

export default Url;
