import './App.css';
import URL from './components/Url'
import Input from './components/input'
import Button from './components/outbutton'
import Output from './components/output'

function App() {
  const style = {
    backgroundColor:'linear-gradient(45deg,#d2001a,#7462ff,#f48e21,#23d5ab)',
    backgroundSize:'300% 300%',
    height:'100%',
    color:"#C2D9FF",
    padding:"5em",
    animation:'color 12s ease-in-out infinite'
  };
  const styles=`
  @keyframes color{
    0%{
      backgroundPosition:0 50%;
    }
    50%{
      backgroundPosition:100% 50%;
    }
    100%{
      backgroundPosition:0 50%;
    }

  }`
  return (
    <div style={style}>
      <style>{styles}</style>
      <div>
      <URL/>
      <Input/>
      <Button/>

      </div>
      <div>
      <Output/>
      </div>

    </div>

  );
}

export default App;
