import React,{ useState } from 'react';

const InputComponent = () => {
  const containerStyle = {
    padding: '30px',
    display: 'flex',
    justifyContent: 'center',
    alignItems:'center',
    flexDirection:'column'
  };

  const inputStyle = {
    padding: '0.5em',
    width:'80%',
    borderRadius:'15px',
    height:'2em',
    animation: 'seIn 4s ease-out', // ease-in is the easing function
};
const buttonStyle = {
  marginTop:'10px',
  fontSize: '1em',
  padding: '10px',
  animation: 'easeIn 2s ease-out',
  backgroundColor:'#190482',
  color:'#C2D9FF',
  borderRadius:'5px',
  borderColor:'#7752FE'

};

// Define the CSS keyframes for the ease-in animation
const styles = `
  @keyframes seIn {
    0% { opacity: 0; transform: translateY(-120px); }
    100% { opacity: 1; transform: translateY(0); }
  }
`;
const [userInput, setUserInput] = useState('');

const handleInputChange = (event) => {
  setUserInput(event.target.value);
};

const handleSubmit = async (event) => {
  event.preventDefault();

  try{
    const response = await fetch('http://localhost:3001/submit',{
      method : 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
      body: JSON.stringify({ userInput }),
    });
    if (response.ok){
      console.log('Input successfully sent to the server!');
    }else{
      console.log('Failed to send the input to the server.');
    } 
  }catch (error){
      console.log('Error sending the input to the server:',error);
    }
  }


  return (
    <div style={containerStyle}>
      <form onSubmit={handleSubmit}>
        <style>{styles}</style>
      <input type="text"  style={inputStyle} value={userInput}  onChange={ handleInputChange } placeholder="Enter the URL"/>
      <button type="submit" style={buttonStyle}>Click to Shorten</button>
      </form>
    </div>
  );

  };
export default InputComponent;
