import React from 'react';

const Url = () => {
  const parent = {
    fontSize: '26px', // Set the font size of the parent element
  };

  const head = {
    fontSize: '1em',
    textAlign: 'center',
    animation: 'rotate 2s linear infinite', // 1em will be relative to the font size of the parent
  };

  const styles = `
    @keyframes rotate {
      from {
        transform: rotate(0deg);
      }
      to {
        transform: rotate(360deg);
      }
    }
    
    h1 {
      animation: rotate 4s linear infinite;
    }
  `;

  return (
    <div style={parent}>
      <style>{styles}</style>
      <h1 style={head}>URL Shortener</h1>
    </div>
  );
};

export default Url;
