import './App.css';
import URL from './components/Url';
import Input from './components/input';
import Button from './components/outbutton';
import Output from './components/output';

function App() {
  const containerStyle = {
    display: 'flex',
    height: '100vh',

  };

  const triangleLeftStyle = {
    flex: 1,
    clipPath: 'polygon(0 0, 100% 0, 0 100%)',
    backgroundColor: '#790489', // Color for the left triangle
    padding: '5em',
    color: '#C2D9FF',
    position: 'relative',
  };

  const triangleRightStyle = {
    flex: 1,
    clipPath: 'polygon(100% 0, 100% 100%, 0 100%)',
    backgroundColor: '#008080', // Color for the right triangle
    padding: '5em',
    color: '#FFFFFF',
    position: 'relative',
  };

  return (
    <div style={containerStyle}>
      <div style={triangleLeftStyle}>
        <URL />
        <Input />
        <Button />
      </div>
      <div style={triangleRightStyle}>
        <Output />
      </div>
    </div>
  );
}

export default App;
