import React, { useState } from 'react';

const Url = () => {
  const [fontSize, setFontSize] = useState(16);

  const parent = {
    fontSize: `${fontSize}px`,
  };

  const head = {
    fontSize: '1em',
  };

  const handleFontSizeChange = () => {
    // Example: dynamically change font size on button click
    setFontSize(prevSize => prevSize - 2);
  };

  return (
    <div style={parent}>
      <h1 style={head}>URL Shortener</h1>
      <button onClick={handleFontSizeChange}>Decrease Font Size</button>
    </div>
  );
};

export default Url;
