import './App.css';
import URL from './components/Url';
import Input from './components/input';
import Button from './components/outbutton';
import Output from './components/output';

function App() {
  const containerStyle = {
    backgroundColor: 'linear-gradient(45deg,#d2001a,#7462ff,#f48e21,#23d5ab)',
    backgroundSize: '300% 300%',
    height: '100vh',
    color: '#C2D9FF',
    padding: '5em',
  };

  const contentStyle = {
    animation: 'color 12s ease-in-out infinite',
  };

  const styles = `
    @keyframes color {
      0% {
        background-position: 0 50%;
      }
      50% {
        background-position: 100% 50%;
      }
      100% {
        background-position: 0 50%;
      }
    }
  `;

  return (
    <div style={containerStyle}>
      <style>{styles}</style>
      <div style={contentStyle}>
        <URL />
        <Input />
        <Button />
        <Output />
      </div>
    </div>
  );
}

export default App;
