import React from 'react';

const Outbutton = () => {
  const containerStyle = {
    textAlign: 'center', // Center the button within the container
  };

  const buttonStyle = {
    padding: '0.5em',
    width: '5%',
    fontSize: '1em', // Set the font size using em for responsiveness
  };

  return (
    <div style={containerStyle}>
      <button style={buttonStyle}>Shorten</button>
    </div>
  );
};

export default Outbutton;
