import React from 'react';

const Outbutton = () => {
  const containerStyle = {
    textAlign: 'center', // Center the button within the container
  };

  const buttonStyle = {
    padding: '0.5em',
    width: '5%',
    fontSize: '1em',
    backgroundsize:'contain'
  };

  const responsiveButtonStyle = {
    /* Add responsive styles here */
    width: '10%', // Adjust the width for smaller screens
    fontSize: '0.8em', // Adjust the font size for smaller screens
  };

  return (
    <div style={containerStyle}>
      <button style={{ ...buttonStyle, ...responsiveButtonStyle }}>Shorten</button>
    </div>
  );
};

export default Outbutton;
